    @Test
    fun exactDomainResponseIsNotNull() {
        Truth.assertThat(payloadExactDomain != null)
        payloadExactDomain?.run {
            Truth.assertThat(products).isNotNull()
            Truth.assertThat(domain).isNotNull()
        }
    }

    @Test
    fun domainHasAllParam() {
         payloadExactDomain?.run {
            Truth.assertThat(domain.fqdn).isNotNull()
            Truth.assertThat(domain.isAvailable).isNotNull()
            Truth.assertThat(domain.isPremium).isNotNull()
            Truth.assertThat(domain.productId).isNotNull()
            Truth.assertThat(domain.tierId).isNotNull()
            Truth.assertThat(domain.tld).isNotNull()
        }
    }

    @Test
    fun exactDomainProductSizeGreaterThanZero() {
        payloadExactDomain?.run {
            Truth.assertThat(products.size).isGreaterThan(0)
        }
    }

    @Test
    fun recommendedDomainResponseIsNotNull() {
        Truth.assertThat(payloadSuggestedDomain != null)
        payloadSuggestedDomain?.run {
            Truth.assertThat(products).isNotNull()
            Truth.assertThat(domains).isNotNull()
        }
    }

    @Test
    fun suggestedDomainListIsGreaterThanZero() {
        payloadSuggestedDomain?.run {
            Truth.assertThat(domains.size).isGreaterThan(0)
        }
    }

    @Test
    fun suggestedProductListIsGreaterThanZero() {
        payloadSuggestedDomain?.run {
            Truth.assertThat(products.size).isGreaterThan(0)
        }
    }
